```C#
using System;
using System.Collections.Generic;
using System.Net;
using HtmlAgilityPack;

class Program
{
    static void Main(string[] args)
    {
        var url = "https://www.yourwebsite.com";
        var web = new HtmlWeb();
        var doc = web.Load(url);

        var linkNodes = doc.DocumentNode.SelectNodes("//a[@href]");
        if (linkNodes == null)
            return;

        var linkList = new List<string>();
        foreach (var link in linkNodes)
        {
            var hrefValue = link.GetAttributeValue("href", string.Empty);
            if (!linkList.Contains(hrefValue))
                linkList.Add(hrefValue);
        }

        foreach(var link in linkList)
        {
            Uri uriResult;
            bool result = Uri.TryCreate(link, UriKind.Absolute, out uriResult)
                && (uriResult.Scheme == Uri.UriSchemeHttp || uriResult.Scheme == Uri.UriSchemeHttps);

            if (result)
            {
                CheckLinkStatus(link);
                Console.WriteLine("Domain: " + uriResult.Host);
            }
        }

        Console.ReadLine();
    }

    private static void CheckLinkStatus(string link)
    {
        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(link);
        request.Timeout = 15000;
        try
        {
            var response = (HttpWebResponse)request.GetResponse();
            Console.WriteLine("Link: " + link + " Status: " + response.StatusCode);
            response.Close();
        }
        catch
        {
            Console.WriteLine("Link: " + link + " Status: Broken");
        }
    }
}
```

