def is_valid_email(email):
    # Split the email into local and domain parts
    local_part, domain_part = email.split('@')

    # Check for exactly one '@' symbol
    if email.count('@') != 1:
        return False

    # Check the local part for valid characters
    for char in local_part:
        if not (char.isalnum() or char in ['.', '_', '-']):
            return False

    # Check the domain part for valid characters and at least one period
    periods_found = False
    consecutive_periods = False
    for char in domain_part:
        if char == '.':
            if consecutive_periods:
                return False
            periods_found = True
            consecutive_periods = True
        elif not char.isalnum():
            return False
        else:
            consecutive_periods = False

    # Check if at least one period was found in the domain part
    if not periods_found:
        return False

    return True

