sub string_and {
    # Reading input strings
    my ($x, $y) = @_; 
    my $result = '';

    # Iterating over the length of the string
    for my $i (0 .. (length $x) - 1) {
        # If the characters at the same position of both strings are 1, add 1 to $result, else add 0
        $result .= (substr($x, $i, 1) eq '1' && substr($y, $i, 1) eq '1') ? '1' : '0';
    }

    return $result;
}

sub string_or {
    # Reading input strings
    my ($x, $y) = @_;
    my $result = '';
    my $count=0;

    # iterating over the length of the string
    for my $i (0 .. (length $x) - 1) {
        # if either of the characters at the same position of both strings is 1, add 1 to $result. Else, add 0
        $result .= (substr($x, $i, 1) eq '1' || substr($y, $i, 1) eq '1') ? '1' : '0';
    }

    # Now iterate over the string $result. 
    for my $i (0 .. (length $result) - 1) {
        $count++ if (substr($result, $i, 1) eq '1'); # If the current character is '1', increment $count 
    }
    return $count;
}
