// As both x and y are positive, there's no need for abs
// Check whether GCD (gcd = greatest common divisor) is greater than zero to prevent division by zero
int lowest_common_multiple(int x, int y) {
    int gcd_value = gcd(x, y);
    return gcd_value ? (x * y) / gcd_value : 0;
}

int main() {
    printf("%d\n", lowest_common_multiple(3, 5)); // expected output: 15
    printf("%d\n", lowest_common_multiple(25, 15)); // expected output: 75
    return 0;
}
